<?php

namespace AppBundle\Repository;

/**
 * MembreRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class MembreRepository extends \Doctrine\ORM\EntityRepository
{
  /**
    * RequÃªte de recherche du menu de la rubrique membre
    *
    * Author: Delrodie AMOIKON
    * Date: 071/02/2017
    * Since: v1.0
    */
    public function getMenu()
    {
        $em = $this->getEntityManager();
        $qb = $em->createQuery('
            SELECT m
            FROM AppBundle:Membre m
            WHERE m.statut = :stat
        ')
          ->setParameter('stat', 1)
        ;
        try {
            $result = $qb->getResult();

            return $result;

        } catch (NoResultException $e) {
            return $e;
        }

    }

    /**
    * Recherche de l'article de la rubrique membre
    *
    * Author: Delrodie AMOIKON
    * Date: 07/02/2017
    * Since: v1.0
    */
    public function getArticle($slug)
    {
        $em = $this->getEntityManager();
        $qb = $em->createQuery('
            SELECT m
            FROM AppBundle:Membre m
            WHERE m.slug LIKE :slug
            AND m.statut = :stat
        ')
          ->setParameter('slug', '%'.$slug.'%')
          ->setParameter('stat', 1)
        ;
        try {
            $result = $qb->getResult();

            return $result;

        } catch (NoResultException $e) {
            return $e;
        }
    }
}
